imag.script:

            var storage = $phone.localStorage();
            //手机屏幕参数
            var phone_height;
            var phone_width;
            var density;
            var dp;
            //api地址
            var home_source = new Array(17);
            var video_source = new Array(12);
            var theme_source = new Array(2);
			$page.onload = function(){
				//check_time();
            	dip();//获取屏幕参数
				get_home_page();
				get_video_page();
				get_theme_page();
			}
			//获取话题页
			function get_theme_page(){
				get_theme_page_source();
				get_theme_data('theme_list0',theme_source[0]);
			}
			function get_theme_page_source(){
				theme_source[0] = "http://api.1-blog.com/biz/bizserver/xiaohua/list.do";//推荐（笑话接口）
			}
			function get_theme_data(list,source){
				$http.get(source,function(data){
					var listData = JSON.parse(data).detail;
					var listJson = {items:[]};
					for (var i = 0;i<listData.length;i++){
						if(listData[i].picUrl!=""){
							var itemJson = {
								template:0,
								widgets:{
									content:{text:listData[i].content},
                                    img0:{
                                        src:listData[i].picUrl,
                                        onclick:'gallery("'+listData[i].picUrl+'")'
                                    },
									author:{text:listData[i].author}
								}
							}
						}else {
							var itemJson = {
								template:1,
								widgets:{
									content:{text:listData[i].content},
									author:{text:listData[i].author}
								}
							}
						}
						listJson.items.push(itemJson);
					}
					$(list).update(listJson);
				})
			}
			//获取视频页内容get_video_page()
			function get_video_page(){
				get_video_page_source();
				get_video_data('video_list0',video_source[0]);
				//adjust_video_pic_size('video_1');
			}
			function get_video_page_source(){
				video_source[0] = "http://api.sina.cn/sinago/list.json?channel=video_funny";//推荐
			}
			function get_video_data(list,source){
				$http.get(source,function(data){
                    //alert(data);
					var listData = JSON.parse(data).data.list;
					var listJson = {items:[]};
					for(var i = 0;i<listData.length;i++){
						var link = listData[i].video_info.url;
						var title = listData[i].title;
						var runtime = check_time(parseInt(listData[i].video_info.runtime));
						//add_video_info(title,i);
						var itemJson = {
							template:0,
							id:'video_item'+i,
							onclick:'web_open("'+link+'","'+title+'")',
							widgets:{
								title:{text:title},
								kpic:{src:listData[i].kpic,
										id:'video_'+i},
								source:{text:'来源：'+listData[i].source},
								runtime:{text:runtime},
								playnumber:{text:listData[i].video_info.playnumber+'次播放'}
							}
						}
						listJson.items.push(itemJson);
					}
					$(list).update(listJson);
				})
			}
			//解析视频播放时间
			function check_time(runtime){
				runtime = parseInt(runtime/1000);
				var hour = parseInt(runtime/60);
				var minute = runtime-hour*60;
				if(hour<10){hour='0'+hour;}
				if(minute<10){minute='0'+minute;}
				return hour+':'+minute;
			}
			//视频图上方文字和时长relative
			function add_video_info(title,i){
				var video_title = $C('<label style="font-size:14;color:#ffffff;margin:14 15" position="topleft" relative="video_'+i+'">'+title+'</label>'+
                        '<icon src="video_play.png" position="middlecenter" relative="video_'+i+'"/>');
				$('video_content0').add(video_title);
			}
        	//视频页图片尺寸
			function adjust_video_pic_size(id){
				var pic_width = parseInt(phone_width/dp);
                var pic_height = parseInt(phone_width*0.5625/dp);
                alert(pic_height+'前面是高度，后面是宽度'+pic_width);
                //$('video_1').css({'height':'810px','width':'1440px'});
                $('video_1').css('height:'+pic_height+';width:'+pic_width);
			}
			//获取首页内容get_home_page()
			function get_home_page(){
				//$('home_content').clear();
				get_home_page_source();
            	//get_home_data('home_list0',home_source[0]);
				//adjust_home_pic_size('big_img_show');
                for(i = 0;i<7;i++){
            		get_home_data('home_list'+i,home_source[i]);
                }
			}
            function get_home_page_source(){
				home_source[0] = "http://api.sina.cn/sinago/list.json?channel=news_toutiao";//推荐
           		home_source[1] = "http://api.sina.cn/sinago/list.json?channel=news_tech";//科技（竞技）
           		home_source[2] = "http://api.sina.cn/sinago/list.json?channel=news_sports";//体育
           		home_source[3] = "http://api.sina.cn/sinago/list.json?channel=news_auto";//汽车
           		home_source[4] = "http://api.sina.cn/sinago/list.json?channel=news_finance";//财经
           		home_source[5] = "http://api.sina.cn/sinago/list.json?channel=news_funny";//搞笑
           		home_source[6] = "http://api.sina.cn/sinago/list.json?channel=news_ent";//娱乐
           		home_source[7] = "";//汽车
           		home_source[8] = "";//财经
           		home_source[9] = "";//明显
           		home_source[10] = "";//八卦
           		home_source[11] = "";//娱乐
           		home_source[12] = "";//趣图
           		home_source[13] = "";//美女
           		home_source[14] = "";//健康
           		home_source[15] = "";//正能量
           		home_source[16] = "";//房产
            }
			function get_home_data(list,source){
				$http.get(source,function(data){
                    //alert(data);
					var listData = JSON.parse(data).data.list;
					var listJson = {items:[]};
					for(var i = 0;i<listData.length;i++){
						if(listData[i].feedShowStyle=='common'){
                            var link = listData[i].link;
                            var title = listData[i].title;
							//验证评论是否存在
							if(listData[i].comment_count_info!=null&&listData[i].comment_count_info!=""){
								var comment_count = listData[i].comment_count_info.total;
							}else {
								var comment_count = "";
							}
							if(listData[i].pics!=null&&listData[i].pics!=""){
								var pics = listData[i].pics;
								var itemJson = {
									template:0,
									id:'home_item'+i,
									onclick:'web_open("'+link+'","'+title+'")',
									widgets:{
										title:{text:title},
										kpic0:{src:pics.list[0].kpic},
										kpic1:{src:pics.list[1].kpic},
										kpic2:{src:pics.list[2].kpic},
										source:{text:'来源：'+listData[i].source},
										comment_count:{text:comment_count+'评论'},
										delete:{oncilck:'hidden_menu("item'+i+'")'}
									}
								}
							}else{
								var itemJson = {
									template:1,
									id:'home_item'+i,
									onclick:'web_open("'+link+'","'+title+'")',
									widgets:{
										title:{text:title},
										source:{text:'来源：'+listData[i].source},
										comment_count:{text:comment_count+'评论'},
										kpic:{src:listData[i].kpic},
										delete:{oncilck:'hidden_menu("item'+i+'")'}
									}
								}
							}
							listJson.items.push(itemJson);
						}else if(listData[i].feedShowStyle=='big_img_show'){
                            var link = listData[i].link;
                            var title = listData[i].title;
                            var itemJson = {
                                template:2,
                                id:'big_img_show'+i,
								onclick:'web_open("'+link+'","'+title+'")',
                                widgets:{
									ad_title:{text:title},
									bpic:{src:listData[i].bpic},
									delete:{oncilck:'hidden_menu("big_img_show'+i+'")'}
                            	}
                            }
							listJson.items.push(itemJson);
                        }
					}
					$(list).update(listJson);
				})
			}
			//修改首页推广图片尺寸
			function adjust_home_pic_size(id){
                var img_width = parseInt(phone_width/dp-32);
                var img_height = parseInt(img_width/1.77);
                //alert('图片宽度：'+img_width+'，图片高度：'+img_height);
                $(id).css('width:'+img_width+';height:'+img_height);				
			}
			//以下通用方法
			function web_open(link,title){
				$page.open('web.xml');
				storage.setItem('link', link);
				storage.setItem('title', title);
			}
			function hidden_menu(id){
				var hidden_menu = $C('<popupmenu id="hidden_menu">'+
										'<list><item onclick="hidden('+id+');" style="corner-radius:6">'+
           									'<label style="align:center">不感兴趣并隐藏</label></item>'+
           								'</list>'+
									'</popupmenu>');
				hidden_menu.open();
			}
			//查看大图
			function gallery(url){
                //alert('图片地址为：'+url);
				$page.gallery([{src:url}]);
			}
			//类别选择
			function hidden(id){
           		$('hidden_menu').close();
           		hint('已隐藏');
				$(id).css("display:none");
			}
        	//获取屏幕参数
            function dip(){
                var info = $phone.info();
                phone_height = info['screenHeight'];
                phone_width = info['screenWidth'];
                density = info['density'];
                switch(density){
                    case 'mdpi':
                        dp = 1;
                        break;
                    case 'ldpi':
                        dp = 1.33;
                        break;
                    case 'hdpi':
                        dp = 1.5;
                        break;
                    case 'xhdpi':
                        dp = 2;
                        break;
                    case 'xxhdpi':
                        dp = 3.5;
                        break;
                    case 'xxxhdpi':
                        dp = 4;
                        break;
                }
            }
