				storage.setItem('link', 'http://api.sina.cn/sinago/video_location.json?video_id=c~Swo0zetjJCczOIyLeecg__.mp4?vend=miaopai&c~Swo0zetjJCczOIyLeecg__&video_play_url=http%3A%2F%2Fgslb.miaopai.com%2Fstream%2Fc%7ESwo0zetjJCczOIyLeecg__.mp4%3Fvend%3Dmiaopai%26c%7ESwo0zetjJCczOIyLeecg__.mp4&video_cate=miaopai&docID=fxtrwtu9915419&col=gossip&fromsinago=1&postt=news_video_gossip_1&from=');
				storage.setItem('title', '曝王菲将出新歌 没宣传很任性');				
			}
